import json
from web3 import Web3

address='0xd5E0bda3ff44E57c78B2125A1037F177e78fb365' # contract address

ganache_url='HTTP://127.0.0.1:7545'
web3 = Web3(Web3.HTTPProvider(ganache_url))

web3.eth.default_account=web3.eth.accounts[0]

abi=json.loads('[{"inputs":[],"name":"greet","outputs":[{"internalType":"string","name":"","type":"string"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"greeter","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[],"name":"greeting","outputs":[{"internalType":"string","name":"","type":"string"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"string","name":"_greeting","type":"string"}],"name":"setGreeting","outputs":[],"stateMutability":"nonpayable","type":"function"}]')
bytecode=''

greeter=web3.eth.contract(abi=abi,bytecode=bytecode)

tx_hash=greeter.constructor().transact()

tx_recipt = web3.eth.wait_for_transaction_receipt(tx_hash)

contract= web3.eth.contract(
    address=tx_recipt.contractAddress,
    abi=abi
)

print(contract.functions.greet().call())

tx_hash=contract.functions.setGreeting('hello').transact()

tx_recipt = web3.eth.wait_for_transaction_receipt(tx_hash)

print(contract.functions.greet().call())